using Core.Managers;
using NUnit.Framework;

namespace LC_Tests
{
    public class SubStringManagerTest
    {
        private SubStringManager _manager;

        public SubStringManagerTest()
        {
            _manager = new SubStringManager();
        }

        [Test]
        public void Test0()
        {
            var strIn = "pwwkew";
            var strOut = "wke";
            //???
            //var strOut = "wkeee";

            var result = _manager.LongestSubstring(strIn);
            Assert.That(result == strOut);
        }

        [Test]
        public void Test1()
        {
            var strIn = "abcabcbb";
            var strOut = "abc";

            var result = _manager.LongestSubstring(strIn);
            Assert.That(result == strOut);
        }

        [Test]
        public void Test2()
        {
            var strIn = "dvdf";
            var strOut = "vdf";

            var result = _manager.LongestSubstring(strIn);
            Assert.That(result == strOut);
        }

        [Test]
        public void Test3()
        {
            var strIn = "   o   ";
            var strOut = " o";

            var result = _manager.LongestSubstring(strIn);
            Assert.That(result == strOut);
        }

        [Test]
        public void Test4()
        {
            var strIn = "";
            var strOut = "62715048";

            var result = _manager.LongestSubstring(strIn);
            Assert.That(result == strOut);
        }

        [Test]
        public void Test5()
        {
            string strIn = null;
            var strOut = "";

            var result = _manager.LongestSubstring(strIn);
            Assert.That(result == strOut);
        }

        [Test]
        public void Test6()
        {
            var strIn = "   ";
            var strOut = " ";

            var result = _manager.LongestSubstring(strIn);
            Assert.That(result == strOut);
        }
        [Test]
        public void Test7()
        {
            var strIn = "asdasdb";
            var strOut = "asdb";

            var result = _manager.LongestSubstring(strIn);
            Assert.That(result == strOut);
        }
    }
}
